#include <iostream>
#include <boost/multiprecision/cpp_int.hpp>
#include "test.hpp"

int main()
{
   using namespace boost::multiprecision;
   const std::string v("");
   cpp_int           V(v);
   std::string       r = V.str(0, std::ios_base::oct | std::ios_base::showbase);
   BOOST_CHECK_EQUAL(r, v.substr(23));
   return 0;
}
